// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/token/ERC721/extensions/ERC721Enumerable.sol";
import "@openzeppelin/contracts/token/ERC721/extensions/ERC721URIStorage.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/security/Pausable.sol";
import "@openzeppelin/contracts/utils/Counters.sol";
import "@openzeppelin/contracts/utils/Strings.sol";

/**
 * @title NFTReceipt
 * @dev Smart contract  mint NFT receipts cho n hng
 * @author E-Commerce Blockchain Team
 */
contract NFTReceipt is
    ERC721,
    ERC721Enumerable,
    ERC721URIStorage,
    AccessControl,
    ReentrancyGuard,
    Pausable
{
    using Counters for Counters.Counter;
    using Strings for uint256;

    bytes32 public constant MINTER_ROLE = keccak256("MINTER_ROLE");
    bytes32 public constant ADMIN_ROLE = keccak256("ADMIN_ROLE");

    struct Receipt {
        bytes32 orderHash;
        string metadataUrl;
        uint256 timestamp;
        address minter;
        bool isActive;
    }

    Counters.Counter private _tokenIdCounter;
    mapping(bytes32 => uint256) public orderHashToTokenId;
    mapping(uint256 => Receipt) public receipts;
    mapping(address => uint256[]) public ownerTokens;
    string private _baseTokenURI;

    // Events
    event OrderReceiptMinted(
        bytes32 indexed orderHash,
        uint256 indexed tokenId,
        address indexed owner,
        address minter,
        string metadataUrl
    );

    event ReceiptMetadataUpdated(
        uint256 indexed tokenId,
        string oldMetadataUrl,
        string newMetadataUrl
    );

    event ReceiptRevoked(
        uint256 indexed tokenId,
        address indexed owner,
        string reason
    );

    event ReceiptRestored(
        uint256 indexed tokenId,
        address indexed owner
    );

    event Received(address sender, uint amount);

    constructor(string memory baseURI) ERC721("OrderReceipt", "ORCPT") {
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(ADMIN_ROLE, msg.sender);
        _grantRole(MINTER_ROLE, msg.sender);
        _baseTokenURI = baseURI;
    }

    /**
     * @dev Mint NFT receipt cho n hng
     * @param to a ch nhn NFT
     * @param orderHash Hash ca n hng
     * @param metadataUrl URL ca metadata
     * @return tokenId ID ca NFT c mint
     */
    function safeMint(
        address to,
        bytes32 orderHash,
        string memory metadataUrl
    )
        external
        onlyRole(MINTER_ROLE)
        nonReentrant
        whenNotPaused
        returns (uint256)
    {
        require(to != address(0), "Invalid recipient address");
        require(orderHash != bytes32(0), "Invalid order hash");
        require(bytes(metadataUrl).length > 0, "Metadata URL required");
        require(orderHashToTokenId[orderHash] == 0, "Order already minted");

        _tokenIdCounter.increment();
        uint256 tokenId = _tokenIdCounter.current();

        _safeMint(to, tokenId);
        _setTokenURI(tokenId, metadataUrl);

        receipts[tokenId] = Receipt({
            orderHash: orderHash,
            metadataUrl: metadataUrl,
            timestamp: block.timestamp,
            minter: msg.sender,
            isActive: true
        });

        orderHashToTokenId[orderHash] = tokenId;
        ownerTokens[to].push(tokenId);

        emit OrderReceiptMinted(orderHash, tokenId, to, msg.sender, metadataUrl);

        return tokenId;
    }

    /**
     * @dev Cp nht metadata URL cho NFT
     * @param tokenId ID ca token cn cp nht
     * @param newMetadataUrl URL mi ca metadata
     */
    function updateMetadata(
        uint256 tokenId,
        string memory newMetadataUrl
    )
        external
        onlyRole(ADMIN_ROLE)
        whenNotPaused
    {
        require(_exists(tokenId), "Token does not exist");
        require(bytes(newMetadataUrl).length > 0, "Invalid metadata URL");

        string memory oldUrl = tokenURI(tokenId);
        _setTokenURI(tokenId, newMetadataUrl);
        receipts[tokenId].metadataUrl = newMetadataUrl;

        emit ReceiptMetadataUpdated(tokenId, oldUrl, newMetadataUrl);
    }

    /**
     * @dev Thu hi NFT (ch admin)
     * @param tokenId ID ca token cn thu hi
     * @param reason L do thu hi
     */
    function revokeReceipt(
        uint256 tokenId,
        string memory reason
    )
        external
        onlyRole(ADMIN_ROLE)
        whenNotPaused
    {
        require(_exists(tokenId), "Token does not exist");
        require(receipts[tokenId].isActive, "Token already revoked");

        address owner = ownerOf(tokenId);
        receipts[tokenId].isActive = false;
        _burn(tokenId);

        emit ReceiptRevoked(tokenId, owner, reason);
    }

    /**
     * @dev Khi phc NFT  b thu hi
     * @param to a ch nhn li NFT
     * @param orderHash Hash ca n hng
     * @param metadataUrl URL metadata
     */
    function restoreReceipt(
        address to,
        bytes32 orderHash,
        string memory metadataUrl
    )
        external
        onlyRole(ADMIN_ROLE)
        whenNotPaused
        returns (uint256)
    {
        require(to != address(0), "Invalid recipient");
        require(orderHash != bytes32(0), "Invalid order hash");
        require(bytes(metadataUrl).length > 0, "Metadata URL required");

        uint256 tokenId = orderHashToTokenId[orderHash];
        require(tokenId != 0, "No token for this order");
        require(!receipts[tokenId].isActive, "Token is already active");

        receipts[tokenId].metadataUrl = metadataUrl;
        receipts[tokenId].isActive = true;
        receipts[tokenId].minter = msg.sender;
        receipts[tokenId].timestamp = block.timestamp;

        _safeMint(to, tokenId);
        _setTokenURI(tokenId, metadataUrl);

        emit ReceiptRestored(tokenId, to);
        return tokenId;
    }

    /**
     * @dev Tm dng contract
     */
    function pause() external onlyRole(ADMIN_ROLE) {
        _pause();
    }

    /**
     * @dev Tip tc hot ng contract
     */
    function unpause() external onlyRole(ADMIN_ROLE) {
        _unpause();
    }

    /**
     * @dev Thit lp base URI
     * @param baseURI URI c s mi
     */
    function setBaseURI(string memory baseURI) external onlyRole(ADMIN_ROLE) {
        _baseTokenURI = baseURI;
    }

    // Override required by Solidity
    function _beforeTokenTransfer(
        address from,
        address to,
        uint256 firstTokenId,
        uint256 batchSize
    ) internal virtual override(ERC721, ERC721Enumerable) {
        super._beforeTokenTransfer(from, to, firstTokenId, batchSize);
    }

    // Override required by Solidity
    function _burn(uint256 tokenId) 
        internal 
        override(ERC721, ERC721URIStorage) 
    {
        super._burn(tokenId);
    }

    // Override required by Solidity
    function tokenURI(uint256 tokenId)
        public
        view
        override(ERC721, ERC721URIStorage)
        returns (string memory)
    {
        return super.tokenURI(tokenId);
    }

    // Override required by Solidity
    function supportsInterface(bytes4 interfaceId)
        public
        view
        override(ERC721, ERC721Enumerable, ERC721URIStorage, AccessControl)
        returns (bool)
    {
        return super.supportsInterface(interfaceId);
    }

    // Receive function to handle incoming ETH
    receive() external payable {
        emit Received(msg.sender, msg.value);
    }

    // Fallback function
    fallback() external payable {
        emit Received(msg.sender, msg.value);
    }
}

contracts/PaymentContract.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

/**
 * @title PaymentContract
 * @dev Smart contract for handling e-commerce payments via blockchain
 * @author E-Commerce Blockchain Team
 */
contract PaymentContract is ReentrancyGuard, Ownable {

    // Events
    event OrderPaid(
        string indexed orderId,
        address indexed payer,
        uint256 amount,
        address indexed token,
        string paymentMethod,
        uint256 timestamp
    );

    event PaymentFailed(
        string indexed orderId,
        address indexed payer,
        string reason,
        uint256 timestamp
    );

    event EmergencyPause(address indexed caller, uint256 timestamp);
    event EmergencyUnpause(address indexed caller, uint256 timestamp);

    // State variables
    mapping(address => bool) public authorizedTokens;
    mapping(string => bool) public processedOrders;

    bool public paused = false;
    address public recipientWallet;

    // Local test tokens (deployed on Hardhat local network)
    address public USDT_TOKEN;
    address public USDC_TOKEN;
    address public constant NATIVE_TOKEN = 0x0000000000000000000000000000000000000000; // ETH

    // Exchange rates (mock - for local testing)
    uint256 public USDT_RATE = 25000; // 1 USDT = 25,000 VND
    uint256 public USDC_RATE = 25000; // 1 USDC = 25,000 VND
    uint256 public ETH_RATE = 850000000; // 1 ETH = 850,000,000 VND (mock)

    // Modifiers
    modifier whenNotPaused() {
        require(!paused, "Contract is paused");
        _;
    }

    modifier onlyValidToken(address token) {
        require(
            token == USDT_TOKEN || 
            token == USDC_TOKEN || 
            token == NATIVE_TOKEN,
            "Unsupported token"
        );
        _;
    }

    modifier onlyValidOrder(string memory orderId) {
        require(bytes(orderId).length > 0, "Invalid order ID");
        require(!processedOrders[orderId], "Order already processed");
        _;
    }

    // Constructor
    constructor(address _recipientWallet) {
        require(_recipientWallet != address(0), "Invalid recipient wallet");
        recipientWallet = _recipientWallet;

        // Authorize default tokens
        authorizedTokens[USDT_TOKEN] = true;
        authorizedTokens[USDC_TOKEN] = true;
        authorizedTokens[NATIVE_TOKEN] = true;
    }

    /**
     * @dev Pay for order using ERC20 token (USDT/USDC)
     * @param orderId Unique order identifier
     * @param token Token address (USDT or USDC)
     * @param amount Amount in token units (with decimals)
     */
    function payOrderWithToken(
        string memory orderId,
        address token,
        uint256 amount
    ) 
        external 
        nonReentrant 
        whenNotPaused 
        onlyValidToken(token) 
        onlyValidOrder(orderId)
    {
        require(amount > 0, "Amount must be greater than 0");

        // Transfer token from payer to recipient wallet
        bool success = IERC20(token).transferFrom(msg.sender, recipientWallet, amount);

        if (!success) {
            emit PaymentFailed(orderId, msg.sender, "Token transfer failed", block.timestamp);
            revert("Token transfer failed");
        }

        // Mark order as processed
        processedOrders[orderId] = true;

        // Emit success event
        emit OrderPaid(
            orderId,
            msg.sender,
            amount,
            token,
            "ERC20_TOKEN",
            block.timestamp
        );
    }

    /**
     * @dev Pay for order using native coin (ETH)
     * @param orderId Unique order identifier
     */
    function payOrderWithNative(
        string memory orderId
    )
        external
        payable
        nonReentrant
        whenNotPaused
        onlyValidOrder(orderId)
    {
        require(msg.value > 0, "Amount must be greater than 0");

        // Transfer native coin to recipient wallet
        (bool success, ) = recipientWallet.call{value: msg.value}("");
        require(success, "Native coin transfer failed");

        // Mark order as processed
        processedOrders[orderId] = true;

        // Emit success event
        emit OrderPaid(
            orderId,
            msg.sender,
            msg.value,
            NATIVE_TOKEN,
            "NATIVE_COIN",
            block.timestamp
        );
    }

    /**
     * @dev Withdraw funds (only owner)
     * @param token Token address (use NATIVE_TOKEN for ETH)
     * @param amount Amount to withdraw
     */
    function withdraw(
        address token,
        uint256 amount
    ) external onlyOwner {
        require(amount > 0, "Amount must be greater than 0");

        if (token == NATIVE_TOKEN) {
            require(address(this).balance >= amount, "Insufficient balance");
            payable(owner()).transfer(amount);
        } else {
            require(IERC20(token).balanceOf(address(this)) >= amount, "Insufficient balance");
            IERC20(token).transfer(owner(), amount);
        }
    }

    /**
     * @dev Emergency pause/unpause functions
     */
    function pause() external onlyOwner {
        require(!paused, "Contract already paused");
        paused = true;
        emit EmergencyPause(msg.sender, block.timestamp);
    }

    function unpause() external onlyOwner {
        require(paused, "Contract not paused");
        paused = false;
        emit EmergencyUnpause(msg.sender, block.timestamp);
    }

    /**
     * @dev Add/remove authorized token
     */
    function setTokenAuthorization(
        address token,
        bool authorized
    ) external onlyOwner {
        authorizedTokens[token] = authorized;
    }

    /**
     * @dev Update recipient wallet address
     */
    function setRecipientWallet(address _recipientWallet) external onlyOwner {
        require(_recipientWallet != address(0), "Invalid recipient wallet");
        recipientWallet = _recipientWallet;
    }

    /**
     * @dev Get contract balance for a specific token
     */
    function getBalance(address token) external view returns (uint256) {
        if (token == NATIVE_TOKEN) {
            return address(this).balance;
        }
        return IERC20(token).balanceOf(address(this));
    }

    /**
     * @dev Check if order was already processed
     */
    function isOrderProcessed(string memory orderId) external view returns (bool) {
        return processedOrders[orderId];
    }

    /**
     * @dev Get supported tokens
     */
    function getSupportedTokens() external view returns (address[] memory) {
        address[] memory tokens = new address[](3);
        tokens[0] = USDT_TOKEN;
        tokens[1] = USDC_TOKEN;
        tokens[2] = NATIVE_TOKEN;
        return tokens;
    }

    /**
     * @dev Get exchange rate for a token (VND per token)
     */
    function getExchangeRate(address token) external view returns (uint256) {
        if (token == USDT_TOKEN) return USDT_RATE;
        if (token == USDC_TOKEN) return USDC_RATE;
        if (token == NATIVE_TOKEN) return ETH_RATE;
        return 0;
    }

    /**
     * @dev Fallback function to receive native coins
     */
    receive() external payable {
        // Accept native coin payments
    }
}

